import React from "react";
import 'bootstrap/dist/css/bootstrap.css'

const Login = () => {

    const [player, setPlayer] = useState({fname:"", lname:"", age:"", email:"", contact:"", password:""});

    let field, value;
    const handleInput = (e) =>{
        field = e.target.name;
        value = e.target.value;

        setPlayer({...player,[field]:value})
    }

    const handleSubmit = async (e) =>{
        e.preventDefault();

        const res = await fetch("/players", {
            method: "POST",
            headers: {
                "Content-type" : "application/json"
            },
            body : JSON.stringify({
                player : {fname : player.fname, lname:player.lname, age:player.age, email:player.email, contact:player.contact, password:player.password}
            })
        })

        const data = await res.json()
        if(!data){
            window.alert("Invalid data")
        }
        else{
            // window.alert("registration successfull")
            console.log(data)
        }
    }

    return(
        <div className="container">
            <div className="row">
                <form className="row g-3 needs-validation" noValidate>

                    <div className="col-12">
                    <label htmlFor="validationCustom02" className="form-label">Email</label>
                    <input type="email" className="form-control" id="validationCustom02" defaultValue="Otto" required />
                    <div className="valid-feedback">
                        Looks good!
                    </div>
                    </div>

                    <div className="col-12">
                    <label htmlFor="validationCustom03" className="form-label">Password</label>
                    <input type="password" className="form-control" id="validationCustom02" defaultValue="Otto" required />
                    <div className="valid-feedback">
                        Looks good!
                    </div>
                    </div>

                    <div className="col-12">
                    <button className="btn btn-success" type="submit">Login</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default Login
