import React,{useState} from "react";
import { useParams } from "react-router-dom";

const EditTeam = () => {
    const params = useParams();
    console.log(params)

    const [team, setTeam] = useState({name:"", locationBased:""});

    let field, value;
    const handleInput = (e) =>{
        field = e.target.name;
        value = e.target.value;

        setTeam({...team,[field]:value})
    }

    const handleSubmit = async (e) =>{
        e.preventDefault();

        const res = await fetch("/team", {
            method: "PUT",
            headers: {
                "Content-type" : "application/json"
            },
            body : JSON.stringify({
                player : {name : team.name, locationBased : team.locationBased}
            })
        })

        const data = await res.json()
        if(!data || res.status === 404){
            window.alert("Invalid data")
        }
        else{
            window.alert(data.msg)
            // console.log(data)
        }
    }

    return(
        <div className="container">
            <div className="row">
                <form className="row g-3 needs-validation" method="POST" noValidate autoComplete="off">
                    <div className="col-12">
                    <label htmlFor="validationCustom01" className="form-label">First name</label>
                    <input type="text" className="form-control"  name="name" value={team.name} onChange={handleInput} required />
                    <div className="valid-feedback">
                        Looks good!
                    </div>
                    </div>

                    <div className="col-12">
                    <label htmlFor="validationCustom02" className="form-label">Last name</label>
                    <input type="text" className="form-control"  name="locationBased" value={team.locationBased} onChange={handleInput} required />
                    <div className="valid-feedback">
                        Looks good!
                    </div>
                    </div>

                    <div className="col-12">
                    <button className="btn btn-success" type="submit" onClick={handleSubmit}>Update Team</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default EditTeam
